INSERT INTO `product` VALUES ('3', '干果沙拉', '简单又健康的干果沙拉，做起来一点也不费力，喜欢的亲们可以试试哦~干果除了做饼干、做蒸糕、或直接当零食，原来搭配胡萝卜和玉米做成沙拉也很好吃，嚼起来很有层次感。', '沙拉', '38.00', '66.00', '5', '5', '44', 'N', 'N', 'sala/Dried-fruit-salad.jpg', '100', 'Y', 'N', 'Y', 'Y', '', '2017-08-25 15:26:10', '2017-08-25 15:26:10');
