INSERT INTO `product` VALUES ('102', '桑格利亚鸡尾酒', '桑格利亚（sangría）是西班牙的一种汽酒，也是西班牙的国酒，适宜冰冻饮用，口感稍甜。该酒继承了费城葡萄酒的精神。', '饮品', '40.00', '50.00', '8', '8', '119', 'N', 'Y', 'drink/Sangria-Cocktail.jpeg', '100', 'Y', 'N', '', 'Y', '', '2017-08-25 15:26:10', '2017-08-25 15:26:10');
